mapper.update:
UPDATE free_board

	SET bo_title = #{boTitle}
		, bo_category = #{boCategory}
		, bo_writer = #{boWriter}
		, bo_content = #{boContent}
		, bo_ip = #{boIp}
		, bo_hit = #{boHit}
		, bo_mod_date = sysdate
	WHERE bo_no = #{boNo}

mapper.delete:


UPDATE free_board
SET bo_del_yn = "Y"
WHERE bo_no = #{boNo}

mapper.update:
UPDATE free_board
SET bo_hit = bo_hit + 1 

	WHERE bo_no = #{boNo}
